package ak.core.ui.compnentsimport androidx.compose.material3.Iconimport androidx.compose.material3.MaterialThemeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.painter.Painterimport androidx.compose.ui.graphics.vector.ImageVector@Composablefun Ak_Icon(    modifier: Modifier = Modifier,    icon: Painter? = null,    contentDescription:String = "icon",    tint: Color = MaterialTheme.colorScheme.onSecondary,) {    if (icon != null) {    Icon(        icon,        contentDescription = contentDescription,        modifier = modifier,        tint = tint    )    }}@Composablefun Ak_Icon(    modifier: Modifier = Modifier,    icon: ImageVector? = null,    contentDescription:String = "icon",    tint: Color = MaterialTheme.colorScheme.onSecondary,) {    if (icon != null) {        Icon(            icon,            contentDescription = contentDescription,            modifier = modifier,            tint = tint        )    }}