package ak.core.ui.compnents.dialogsimport androidx.compose.material3.AlertDialogimport androidx.compose.material3.Buttonimport androidx.compose.material3.Textimport androidx.compose.runtime.Composable@Composablefun Ak_NoInternetConnectionDialog(onDismiss: () -> Unit) {    AlertDialog(        onDismissRequest = onDismiss,        title = { Text("لا يوجد اتصال بالإنترنت") },        text = { Text("يرجى التحقق من اتصالك بالإنترنت والمحاولة مرة أخرى.") },        confirmButton = {            Button(onClick = onDismiss) {                Text("إغلاق")            }        }    )}