package ak.core.ui.compnents.dialogsimport androidx.compose.material3.AlertDialogimport androidx.compose.material3.Buttonimport androidx.compose.material3.Textimport androidx.compose.material3.TextButtonimport androidx.compose.runtime.Composable@Composablefun Ak_UpdateAvailableDialog(    onDismiss: () -> Unit,    onUpdateClick: () -> Unit) {    AlertDialog(        onDismissRequest = onDismiss,        title = { Text("يتوفر تحديث جديد") },        text = { Text("هناك إصدار أحدث من التطبيق متاح. هل ترغب في التحديث الآن؟") },        confirmButton = {            Button(onClick = onUpdateClick) {                Text("تحديث")            }        },        dismissButton = {            TextButton(onClick = onDismiss) {                Text("لاحقًا")            }        }    )}