package ak.core.ui.compnents.dialogsimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.LinearProgressIndicatorimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dp@Composablefun DownloadProgressBar(progress: Int) {    LinearProgressIndicator(        progress = { progress / 100f },        modifier = Modifier.fillMaxWidth().padding(16.dp),        color = Color.Blue, // Customize color        trackColor = Color.LightGray, // Customize background color    )}